@Override public List<ParameterSpecification> options(){
  return ImmutableList.of(parameter("configure","If set, will cause the cluster to be automatically " + "configured"),parameter("type","Type of backend to use, valid values are: " + parameters.join(types.keySet()),"<type>"),parameter("seeds","Seeds to use when configuring backend","<host>[:<port>][,..]"),parameter("fetchSize","The number of results to fetch per batch","<int>"),parameter("consistencyLevel","The default consistency level to use",parameters.join(Arrays.stream(ConsistencyLevel.values()).map(cl -> cl.name()).iterator())),parameter("retryPolicy","The retry policy to use (useful when migrating " + "data)","aggressive"),parameter("numRetries","The number of retries to attempt for the current " + "retry policy","<int>"),parameter("rotateHost","The number of retries to attempt before rotating " + "host for the current retry policy","<int>"));
}
