public String createDebugString(){
  return Objects.toStringHelper(this).add("controller ID",mControllerId).add("request ID",mRequestId).add("controller submit",mControllerSubmitTimeMs).add("controller final image",mControllerFinalImageSetTimeMs).add("controller failure",mControllerFailureTimeMs).add("controller cancel",mControllerCancelTimeMs).add("start time",mImageRequestStartTimeMs).add("end time",mImageRequestEndTimeMs).add("origin",ImageOriginUtils.toString(mImageOrigin)).add("ultimateProducerName",mUltimateProducerName).add("prefetch",mIsPrefetch).add("caller context",mCallerContext).add("image request",mImageRequest).add("image info",mImageInfo).add("on-screen width",mOnScreenWidthPx).add("on-screen height",mOnScreenHeightPx).add("visibility state",mVisibilityState).add("component tag",mComponentTag).toString();
}
