@ShouldUpdate(onMount=true) static boolean shouldUpdate(@Prop Diff<Component> childComponent,@Prop(optional=true) Diff<Boolean> scrollbarEnabled,@Prop(optional=true) Diff<Boolean> scrollbarFadingEnabled,@Prop(optional=true) Diff<Boolean> fillViewport,@Prop(optional=true) Diff<Boolean> nestedScrollingEnabled,@Prop(optional=true) Diff<Boolean> incrementalMountEnabled){
  return !childComponent.getPrevious().isEquivalentTo(childComponent.getNext()) || !scrollbarEnabled.getPrevious().equals(scrollbarEnabled.getNext()) || !scrollbarFadingEnabled.getPrevious().equals(scrollbarFadingEnabled.getNext()) || !fillViewport.getPrevious().equals(fillViewport.getNext()) || !nestedScrollingEnabled.getPrevious().equals(nestedScrollingEnabled.getNext()) || !incrementalMountEnabled.getPrevious().equals(incrementalMountEnabled.getNext());
}
