static Component buildComment2(ComponentContext c,boolean expanded){
  return Column.create(c).paddingDip(YogaEdge.ALL,8).child(Row.create(c).alignItems(YogaAlign.CENTER).child(Text.create(c).textSizeSp(16).textStyle(Typeface.BOLD).text("Cristobal Castilla")).child(Row.create(c).widthDip(expanded ? 48 : 24).marginDip(YogaEdge.LEFT,8).paddingDip(YogaEdge.ALL,3).alignItems(YogaAlign.CENTER).child(Column.create(c).positionType(YogaPositionType.ABSOLUTE).positionDip(YogaEdge.LEFT,expanded ? 27 : 3).heightDip(18).widthDip(18).background(buildRoundedRect(c,0xFFB2CFE5,9))).child(Column.create(c).positionType(YogaPositionType.ABSOLUTE).positionDip(YogaEdge.LEFT,expanded ? 15 : 3).heightDip(18).widthDip(18).background(buildRoundedRect(c,0xFF4B8C61,9))).child(Column.create(c).heightDip(18).widthDip(18).background(buildRoundedRect(c,0xFFFFB74B,9))).background(buildRoundedRect(c,Color.WHITE,12)))).child(Text.create(c).textSizeSp(18).text("So awesome!")).background(buildRoundedRect(c,0xFFDDDDDD,20)).build();
}
