@OnCreateLayout static Component onCreateLayout(ComponentContext c,@State boolean autoBoundsTransitionEnabled,@State boolean flag1,@State boolean flag2,@State boolean flag3,@State boolean flag4){
  return Column.create(c).backgroundColor(Color.WHITE).alignItems(YogaAlign.CENTER).paddingDip(YogaEdge.VERTICAL,8).child(Text.create(c).text("ABT " + (autoBoundsTransitionEnabled ? "enabled" : "disabled")).textSizeSp(20).textStyle(Typeface.BOLD).clickHandler(BoundsAnimationComponent.onABTClick(c))).child(Text.create(c).marginDip(YogaEdge.VERTICAL,8).text("Affected Children").textSizeSp(20)).child(affectedChildren(c,flag1)).child(Text.create(c).marginDip(YogaEdge.VERTICAL,8).text("Affected Siblings").textSizeSp(20)).child(affectedSiblings(c,flag2)).child(Text.create(c).marginDip(YogaEdge.VERTICAL,8).text("Affected Parent").textSizeSp(20)).child(affectedParent(c,flag3)).child(altogether(c,flag4)).build();
}
