@OnCreateLayout static Component onCreateLayout(ComponentContext c,@State List<Message> messages,@State int counter){
  return Column.create(c).child(Row.create(c).backgroundColor(Color.LTGRAY).child(Text.create(c).paddingDip(YogaEdge.ALL,10).text("INSERT").textSizeSp(20).flexGrow(1).alignSelf(YogaAlign.CENTER).testKey("INSERT").textAlignment(Layout.Alignment.ALIGN_CENTER).clickHandler(ExpandableElementRootComponent.onClick(c,true))).child(Text.create(c).paddingDip(YogaEdge.ALL,10).text("DELETE").textSizeSp(20).flexGrow(1).alignSelf(YogaAlign.CENTER).textAlignment(Layout.Alignment.ALIGN_CENTER).clickHandler(ExpandableElementRootComponent.onClick(c,false)))).child(RecyclerCollectionComponent.create(c).flexGrow(1).disablePTR(true).itemAnimator(new NotAnimatedItemAnimator()).section(DataDiffSection.<Message>create(new SectionContext(c)).data(messages).renderEventHandler(ExpandableElementRootComponent.onRender(c)).build()).paddingDip(YogaEdge.TOP,8)).build();
}
