@OnCreateLayout static Component onCreateLayout(ComponentContext c,@State RecyclerCollectionEventsController recyclerEventsController,@State DynamicValue<Float> handleTranslation,@State ScrollController scrollController){
  return Column.create(c).backgroundColor(Color.WHITE).child(RecyclerCollectionComponent.create(c).positionType(YogaPositionType.ABSOLUTE).positionPx(YogaEdge.ALL,0).section(CountriesListSection.create(new SectionContext(c)).build()).onScrollListener(scrollController).eventsController(recyclerEventsController).disablePTR(true)).child(buildDragHandle(c).positionType(YogaPositionType.ABSOLUTE).positionDip(YogaEdge.RIGHT,HANDLE_RIGHT_MARGIN).positionDip(YogaEdge.TOP,HANDLE_VERTICAL_MARGIN).translationY(handleTranslation).touchHandler(FastScrollHandleComponent.onTouchEvent(c))).build();
}
