@OnCreateLayout static Component onCreateLayout(ComponentContext c,@Prop Integer[] colors){
  return Column.create(c).paddingDip(YogaEdge.ALL,5).child(Row.create(c).alignContent(YogaAlign.STRETCH).marginDip(YogaEdge.TOP,10).child(Text.create(c).alignSelf(YogaAlign.CENTER).flexGrow(2f).text("Scroll to: ").textSizeSp(20)).child(Text.create(c).alignSelf(YogaAlign.CENTER).flexGrow(0.5f).text("PREVIOUS").clickHandler(HorizontalScrollScrollerComponent.onClick(c,false)).textSizeSp(20)).child(Text.create(c).alignSelf(YogaAlign.CENTER).flexGrow(0.5f).text("NEXT").clickHandler(HorizontalScrollScrollerComponent.onClick(c,true)).textSizeSp(20))).child(Row.create(c).alignContent(YogaAlign.STRETCH).marginDip(YogaEdge.TOP,10).child(Text.create(c).alignSelf(YogaAlign.CENTER).flexGrow(2f).text("Smooth scroll to: ").textSizeSp(20)).child(Spinner.create(c).flexGrow(1.f).options(getPositionsFromDataset(colors)).selectedOption("0").itemSelectedEventHandler(HorizontalScrollScrollerComponent.onScrollToPositionSelected(c)))).build();
}
