@OnCreateLayout static Component onCreateLayout(ComponentContext c,@Prop String title,@Prop String subtitle){
  return Row.create(c).paddingDip(HORIZONTAL,CARD_INSET).paddingDip(TOP,CARD_INSET).child(FrescoImage.create(c).controller(Fresco.newDraweeControllerBuilder().setUri("http://placekitten.com/g/200/200").build()).widthDip(40).heightDip(40).marginDip(END,CARD_INTERNAL_PADDING).marginDip(BOTTOM,CARD_INTERNAL_PADDING)).child(Column.create(c).flexGrow(1f).child(Text.create(c,0,R.style.header_title).text(title).paddingDip(BOTTOM,CARD_INTERNAL_PADDING)).child(Text.create(c,0,R.style.header_subtitle).text(subtitle).paddingDip(BOTTOM,CARD_INTERNAL_PADDING))).child(Image.create(c).drawableRes(R.drawable.menu).clickHandler(StoryHeaderComponent.onClickMenuButton(c)).widthDip(15).heightDip(15).marginDip(START,CARD_INTERNAL_PADDING).marginDip(BOTTOM,CARD_INTERNAL_PADDING)).build();
}
