public static @NonNull MaskedWalletRequest createMaskedWalletRequest(final @NonNull AndroidPayPayload payload){
  return MaskedWalletRequest.newBuilder().setMerchantName(payload.merchantName()).setPhoneNumberRequired(payload.phoneNumberRequired()).setShippingAddressRequired(payload.shippingAddressRequired()).setCurrencyCode(payload.currencyCode()).setAllowDebitCard(payload.allowDebitCard()).setAllowPrepaidCard(payload.allowPrepaidCard()).setEstimatedTotalPrice(payload.estimatedTotalPrice()).setCart(Cart.newBuilder().setCurrencyCode(payload.cart().currencyCode()).setTotalPrice(payload.cart().totalPrice()).setLineItems(lineItemsFromPayload(payload)).build()).setPaymentMethodTokenizationParameters(PaymentMethodTokenizationParameters.newBuilder().setPaymentMethodTokenizationType(PaymentMethodTokenizationType.PAYMENT_GATEWAY).addParameter("gateway","stripe").addParameter("stripe:publishableKey",payload.stripePublishableKey()).addParameter("stripe:version",payload.stripeVersion()).build()).build();
}
