private JobCoreConfiguration getJobCoreConfiguration(final String jobName,final String cron,final int shardingTotalCount,final String shardingItemParameters,final String jobParameter,final boolean failover,final boolean misfire,final String description,final JobProperties jobProperties){
  return JobCoreConfiguration.newBuilder(jobName,cron,shardingTotalCount).shardingItemParameters(shardingItemParameters).jobParameter(jobParameter).failover(failover).misfire(misfire).description(description).jobProperties(JobProperties.JobPropertiesEnum.JOB_EXCEPTION_HANDLER.getKey(),jobProperties.get(JobProperties.JobPropertiesEnum.JOB_EXCEPTION_HANDLER)).jobProperties(JobProperties.JobPropertiesEnum.EXECUTOR_SERVICE_HANDLER.getKey(),jobProperties.get(JobProperties.JobPropertiesEnum.EXECUTOR_SERVICE_HANDLER)).build();
}
