public static MethodSpec.Builder overriding(ExecutableElement method){
  return MethodSpec.methodBuilder(method.getSimpleName().toString()).addAnnotation(Override.class).addModifiers(method.getModifiers().stream().filter(modifier -> modifier != Modifier.ABSTRACT).collect(Collectors.toList())).returns(TypeName.get(method.getReturnType())).varargs(method.isVarArgs()).addExceptions(method.getThrownTypes().stream().map(TypeName::get).collect(Collectors.toList())).addTypeVariables(method.getTypeParameters().stream().map(TypeVariableName::get).collect(Collectors.toList())).addParameters(method.getParameters().stream().map(element -> ParameterSpec.get(element).toBuilder().addAnnotations(element.getAnnotationMirrors().stream().map(AnnotationSpec::get).collect(Collectors.toList())).build()).collect(Collectors.toList()));
}
