@NonNull public static List<FragmentPagerAdapterModel> buildForProfile(@NonNull Context context,@NonNull String login){
  return Stream.of(new FragmentPagerAdapterModel(context.getString(R.string.overview),ProfileOverviewFragment.newInstance(login)),new FragmentPagerAdapterModel(context.getString(R.string.feed),FeedsFragment.newInstance(login,false)),new FragmentPagerAdapterModel(context.getString(R.string.repos),ProfileReposFragment.newInstance(login)),new FragmentPagerAdapterModel(context.getString(R.string.starred),ProfileStarredFragment.newInstance(login)),new FragmentPagerAdapterModel(context.getString(R.string.gists),ProfileGistsFragment.newInstance(login)),new FragmentPagerAdapterModel(context.getString(R.string.followers),ProfileFollowersFragment.newInstance(login)),new FragmentPagerAdapterModel(context.getString(R.string.following),ProfileFollowingFragment.newInstance(login))).collect(Collectors.toList());
}
