@NonNull public static List<FragmentPagerAdapterModel> buildForRepoCode(@NonNull Context context,@NonNull String repoId,@NonNull String login,@NonNull String url,@NonNull String defaultBranch,@NonNull String htmlUrl){
  return Stream.of(new FragmentPagerAdapterModel(context.getString(R.string.readme),ViewerFragment.newInstance(url,htmlUrl,true)),new FragmentPagerAdapterModel(context.getString(R.string.files),RepoFilePathFragment.newInstance(login,repoId,null,defaultBranch)),new FragmentPagerAdapterModel(context.getString(R.string.commits),RepoCommitsFragment.newInstance(repoId,login,defaultBranch)),new FragmentPagerAdapterModel(context.getString(R.string.releases),RepoReleasesFragment.newInstance(repoId,login)),new FragmentPagerAdapterModel(context.getString(R.string.contributors),RepoContributorsFragment.newInstance(repoId,login))).collect(Collectors.toList());
}
