@NonNull public static List<FragmentPagerAdapterModel> buildForOrg(@NonNull Context context,@NonNull String login,boolean isMember){
  return Stream.of(new FragmentPagerAdapterModel(context.getString(R.string.feeds),isMember ? FeedsFragment.newInstance(login,true) : null),new FragmentPagerAdapterModel(context.getString(R.string.overview),OrgProfileOverviewFragment.newInstance(login)),new FragmentPagerAdapterModel(context.getString(R.string.repos),OrgReposFragment.newInstance(login)),new FragmentPagerAdapterModel(context.getString(R.string.people),OrgMembersFragment.newInstance(login)),new FragmentPagerAdapterModel(context.getString(R.string.teams),isMember ? OrgTeamFragment.newInstance(login) : null)).filter(fragmentPagerAdapterModel -> fragmentPagerAdapterModel.getFragment() != null).collect(Collectors.toList());
}
