private void withComments(Comment comment,Context context,Notification thread,int accentColor){
  android.app.Notification toAdd=getNotification(comment.getUser() != null ? comment.getUser().getLogin() : "",MarkDownProvider.stripMdText(comment.getBody()),thread.getRepository() != null ? thread.getRepository().getFullName() : "general").setLargeIcon(BitmapFactory.decodeResource(getResources(),R.mipmap.ic_launcher)).setSmallIcon(R.drawable.ic_notification).setStyle(new NotificationCompat.BigTextStyle().setBigContentTitle(comment.getUser() != null ? comment.getUser().getLogin() : "").bigText(MarkDownProvider.stripMdText(comment.getBody()))).setWhen(comment.getCreatedAt().getTime()).setShowWhen(true).addAction(R.drawable.ic_github,context.getString(R.string.open),getPendingIntent(thread.getId(),thread.getSubject().getUrl())).addAction(R.drawable.ic_eye_off,context.getString(R.string.mark_as_read),getReadOnlyPendingIntent(thread.getId(),thread.getSubject().getUrl())).setContentIntent(getPendingIntent(thread.getId(),thread.getSubject().getUrl())).setColor(accentColor).setGroup(NOTIFICATION_GROUP_ID).build();
  showNotification(thread.getId(),toAdd);
}
