private void buildLogo(Context context,MaterialAboutCard.Builder newLogoAuthor,MaterialAboutCard.Builder logoAuthor){
  newLogoAuthor.title(getString(R.string.logo_designer,"Cookicons"));
  newLogoAuthor.addItem(new MaterialAboutActionItem.Builder().text(R.string.google_plus).icon(ContextCompat.getDrawable(context,R.drawable.ic_profile)).setOnClickAction(() -> ActivityHelper.startCustomTab(this,"https://plus.google.com/+CookiconsDesign")).build()).addItem(new MaterialAboutActionItem.Builder().text(R.string.twitter).icon(ContextCompat.getDrawable(context,R.drawable.ic_profile)).setOnClickAction(() -> ActivityHelper.startCustomTab(this,"https://twitter.com/mcookie")).build()).addItem(new MaterialAboutActionItem.Builder().text(R.string.website).icon(ContextCompat.getDrawable(context,R.drawable.ic_brower)).setOnClickAction(() -> ActivityHelper.startCustomTab(this,"https://cookicons.co/")).build());
  logoAuthor.title(String.format("Old %s",getString(R.string.logo_designer,"Kevin Aguilar")));
  logoAuthor.addItem(new MaterialAboutActionItem.Builder().text(R.string.google_plus).icon(ContextCompat.getDrawable(context,R.drawable.ic_profile)).setOnClickAction(() -> ActivityHelper.startCustomTab(this,"https://plus.google.com/+KevinAguilarC")).build()).addItem(new MaterialAboutActionItem.Builder().text(R.string.twitter).icon(ContextCompat.getDrawable(context,R.drawable.ic_profile)).setOnClickAction(() -> ActivityHelper.startCustomTab(this,"https://twitter.com/kevttob")).build()).addItem(new MaterialAboutActionItem.Builder().text(R.string.website).icon(ContextCompat.getDrawable(context,R.drawable.ic_brower)).setOnClickAction(() -> ActivityHelper.startCustomTab(this,"http://kevaguilar.com/")).build());
}
