private void buildMisc(Context context,MaterialAboutCard.Builder miscCardBuilder){
  miscCardBuilder.title(R.string.about).addItem(new MaterialAboutActionItem.Builder().text(R.string.support_development).icon(ContextCompat.getDrawable(context,R.drawable.ic_heart)).setOnClickAction(() -> startActivity(new Intent(context,DonationActivity.class))).build()).addItem(new MaterialAboutActionItem.Builder().text(R.string.changelog).icon(ContextCompat.getDrawable(context,R.drawable.ic_track_changes)).setOnClickAction(() -> new ChangelogBottomSheetDialog().show(getSupportFragmentManager(),"ChangelogBottomSheetDialog")).build()).addItem(new MaterialAboutActionItem.Builder().text(R.string.join_slack).icon(ContextCompat.getDrawable(context,R.drawable.ic_slack)).setOnClickAction(() -> ActivityHelper.startCustomTab(this,"http://rebrand.ly/fasthub")).build()).addItem(new MaterialAboutActionItem.Builder().text(R.string.open_source_libs).icon(ContextCompat.getDrawable(context,R.drawable.ic_github)).setOnClickAction(() -> new LibsBuilder().withActivityStyle(AppHelper.isNightMode(getResources()) ? Libs.ActivityStyle.DARK : Libs.ActivityStyle.LIGHT).withAutoDetect(true).withActivityTitle(this.getResources().getString(R.string.open_source_libs)).withAboutIconShown(true).withAboutVersionShown(true).start(this)).build());
}
