private void buildApp(Context context,MaterialAboutCard.Builder appCardBuilder){
  appCardBuilder.addItem(new MaterialAboutActionItem.Builder().text(getString(R.string.version)).icon(ContextCompat.getDrawable(context,R.drawable.ic_update)).subText(BuildConfig.VERSION_NAME).setOnClickAction(() -> startService(new Intent(this,CheckVersionService.class))).build()).addItem(ConvenienceBuilder.createRateActionItem(context,ContextCompat.getDrawable(context,R.drawable.ic_star_filled),getString(R.string.rate_app),null)).addItem(new MaterialAboutActionItem.Builder().text(R.string.report_issue).subText(R.string.report_issue_here).icon(ContextCompat.getDrawable(context,R.drawable.ic_bug)).setOnClickAction(() -> CreateIssueActivity.startForResult(this,CreateIssueActivity.startForResult(this),malRecyclerview)).build());
}
