@Override public String getRawCreateScript(Table table,boolean baseline){
  String tablespace=configuration.getTablespace() == null ? "" : " IN \"" + configuration.getTablespace() + "\"";
  return "CREATE TABLE " + table + " (\n" + "    \"installed_rank\" INT NOT NULL,\n" + "    \"version\" VARCHAR(50),\n" + "    \"description\" VARCHAR(200) NOT NULL,\n" + "    \"type\" VARCHAR(20) NOT NULL,\n" + "    \"script\" VARCHAR(1000) NOT NULL,\n" + "    \"checksum\" INT,\n" + "    \"installed_by\" VARCHAR(100) NOT NULL,\n" + "    \"installed_on\" TIMESTAMP DEFAULT CURRENT TIMESTAMP NOT NULL,\n" + "    \"execution_time\" INT NOT NULL,\n" + "    \"success\" SMALLINT NOT NULL,\n" + "    CONSTRAINT \"" + table.getName() + "_s\" CHECK (\"success\" in(0,1))\n" + ")" + " ORGANIZE BY ROW" + tablespace + ";\n" + (baseline ? getBaselineStatement(table) + ";\n" : "") + "ALTER TABLE " + table + " ADD CONSTRAINT \"" + table.getName() + "_pk\" PRIMARY KEY (\"installed_rank\");\n" + "CREATE INDEX \"" + table.getSchema().getName() + "\".\"" + table.getName() + "_s_idx\" ON " + table + " (\"success\");";
}
