@Override public String getRawCreateScript(Table table,boolean baseline){
  return "CREATE TABLE IF NOT EXISTS " + table + " (\n" + "    \"installed_rank\" INT NOT NULL,\n" + "    \"version\" VARCHAR(50),\n" + "    \"description\" VARCHAR(200) NOT NULL,\n" + "    \"type\" VARCHAR(20) NOT NULL,\n" + "    \"script\" VARCHAR(1000) NOT NULL,\n" + "    \"checksum\" INT,\n" + "    \"installed_by\" VARCHAR(100) NOT NULL,\n" + "    \"installed_on\" TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP,\n" + "    \"execution_time\" INT NOT NULL,\n" + "    \"success\" BOOLEAN NOT NULL,\n" + "    CONSTRAINT \"" + table.getName() + "_pk\" PRIMARY KEY (\"installed_rank\")\n" + ")" + " AS SELECT -1, NULL, '<< Flyway Schema History table created >>', 'TABLE', '', NULL, '', CURRENT_TIMESTAMP, 0, TRUE;\n" + (baseline ? getBaselineStatement(table) + ";\n" : "") + "CREATE INDEX \"" + table.getSchema().getName() + "\".\"" + table.getName() + "_s_idx\" ON " + table + " (\"success\");";
}
