/** 
 * Returns the size of the field_info JVMS structure generated by this FieldWriter. Also adds the names of the attributes of this field in the constant pool.
 * @return the size in bytes of the field_info JVMS structure.
 */
int computeFieldInfoSize(){
  int size=8;
  if (constantValueIndex != 0) {
    symbolTable.addConstantUtf8(Constants.CONSTANT_VALUE);
    size+=8;
  }
  size+=Attribute.computeAttributesSize(symbolTable,accessFlags,signatureIndex);
  size+=AnnotationWriter.computeAnnotationsSize(lastRuntimeVisibleAnnotation,lastRuntimeInvisibleAnnotation,lastRuntimeVisibleTypeAnnotation,lastRuntimeInvisibleTypeAnnotation);
  if (firstAttribute != null) {
    size+=firstAttribute.computeAttributesSize(symbolTable);
  }
  return size;
}
