/** 
 * Set a matrix property. <p>This is the C-version of  {@code Assimp::Importer::SetPropertyMatrix()}. In the C interface, properties are always shared by all imports. It is not possible to specify them per import.</p>
 * @param store  Store to modify. Use {@link #aiCreatePropertyStore CreatePropertyStore} to obtain a store.
 * @param szName Name of the configuration property to be set. One of:<br><table><tr><td>{@link #AI_CONFIG_GLOB_MEASURE_TIME}</td><td> {@link #AI_CONFIG_IMPORT_NO_SKELETON_MESHES}</td></tr><tr><td> {@link #AI_CONFIG_PP_SBBC_MAX_BONES}</td><td> {@link #AI_CONFIG_PP_CT_MAX_SMOOTHING_ANGLE}</td></tr><tr><td> {@link #AI_CONFIG_PP_CT_TEXTURE_CHANNEL_INDEX}</td><td> {@link #AI_CONFIG_PP_GSN_MAX_SMOOTHING_ANGLE}</td></tr><tr><td> {@link #AI_CONFIG_IMPORT_MDL_COLORMAP}</td><td> {@link #AI_CONFIG_PP_RRM_EXCLUDE_LIST}</td></tr><tr><td> {@link #AI_CONFIG_PP_PTV_KEEP_HIERARCHY}</td><td> {@link #AI_CONFIG_PP_PTV_NORMALIZE}</td></tr><tr><td> {@link #AI_CONFIG_PP_PTV_ADD_ROOT_TRANSFORMATION}</td><td> {@link #AI_CONFIG_PP_PTV_ROOT_TRANSFORMATION}</td></tr><tr><td> {@link #AI_CONFIG_PP_FD_REMOVE}</td><td> {@link #AI_CONFIG_PP_FD_CHECKAREA}</td></tr><tr><td> {@link #AI_CONFIG_PP_OG_EXCLUDE_LIST}</td><td> {@link #AI_CONFIG_PP_SLM_TRIANGLE_LIMIT}</td></tr><tr><td> {@link #AI_CONFIG_PP_SLM_VERTEX_LIMIT}</td><td> {@link #AI_CONFIG_PP_LBW_MAX_WEIGHTS}</td></tr><tr><td> {@link #AI_CONFIG_PP_DB_THRESHOLD}</td><td> {@link #AI_CONFIG_PP_DB_ALL_OR_NONE}</td></tr><tr><td> {@link #AI_CONFIG_PP_ICL_PTCACHE_SIZE}</td><td> {@link #AI_CONFIG_PP_RVC_FLAGS}</td></tr><tr><td> {@link #AI_CONFIG_PP_SBP_REMOVE}</td><td> {@link #AI_CONFIG_PP_FID_ANIM_ACCURACY}</td></tr><tr><td> {@link #AI_CONFIG_PP_FID_IGNORE_TEXTURECOORDS}</td><td> {@link #AI_CONFIG_PP_TUV_EVALUATE}</td></tr><tr><td> {@link #AI_CONFIG_FAVOUR_SPEED}</td><td> {@link #AI_CONFIG_IMPORT_FBX_READ_ALL_GEOMETRY_LAYERS}</td></tr><tr><td> {@link #AI_CONFIG_IMPORT_FBX_READ_ALL_MATERIALS}</td><td> {@link #AI_CONFIG_IMPORT_FBX_READ_MATERIALS}</td></tr><tr><td> {@link #AI_CONFIG_IMPORT_FBX_READ_TEXTURES}</td><td> {@link #AI_CONFIG_IMPORT_FBX_READ_CAMERAS}</td></tr><tr><td> {@link #AI_CONFIG_IMPORT_FBX_READ_LIGHTS}</td><td> {@link #AI_CONFIG_IMPORT_FBX_READ_ANIMATIONS}</td></tr><tr><td> {@link #AI_CONFIG_IMPORT_FBX_STRICT_MODE}</td><td> {@link #AI_CONFIG_IMPORT_FBX_PRESERVE_PIVOTS}</td></tr><tr><td> {@link #AI_CONFIG_IMPORT_FBX_OPTIMIZE_EMPTY_ANIMATION_CURVES}</td><td> {@link #AI_CONFIG_IMPORT_FBX_EMBEDDED_TEXTURES_LEGACY_NAMING}</td></tr><tr><td> {@link #AI_CONFIG_IMPORT_REMOVE_EMPTY_BONES}</td><td> {@link #AI_CONFIG_FBX_CONVERT_TO_M}</td></tr><tr><td> {@link #AI_CONFIG_IMPORT_GLOBAL_KEYFRAME}</td><td> {@link #AI_CONFIG_IMPORT_MD3_KEYFRAME}</td></tr><tr><td> {@link #AI_CONFIG_IMPORT_MD2_KEYFRAME}</td><td> {@link #AI_CONFIG_IMPORT_MDL_KEYFRAME}</td></tr><tr><td> {@link #AI_CONFIG_IMPORT_MDC_KEYFRAME}</td><td> {@link #AI_CONFIG_IMPORT_SMD_KEYFRAME}</td></tr><tr><td> {@link #AI_CONFIG_IMPORT_UNREAL_KEYFRAME}</td><td> {@link #AI_CONFIG_IMPORT_SMD_LOAD_ANIMATION_LIST}</td></tr><tr><td> {@link #AI_CONFIG_IMPORT_AC_SEPARATE_BFCULL}</td><td> {@link #AI_CONFIG_IMPORT_AC_EVAL_SUBDIVISION}</td></tr><tr><td> {@link #AI_CONFIG_UNREAL_HANDLE_FLAGS}</td><td> {@link #AI_CONFIG_IMPORT_TER_MAKE_UVS}</td></tr><tr><td> {@link #AI_CONFIG_IMPORT_ASE_RECONSTRUCT_NORMALS}</td><td> {@link #AI_CONFIG_IMPORT_MD3_HANDLE_MULTIPART}</td></tr><tr><td> {@link #AI_CONFIG_IMPORT_MD3_SKIN_NAME}</td><td> {@link #AI_CONFIG_IMPORT_MD3_SHADER_SRC}</td></tr><tr><td> {@link #AI_CONFIG_IMPORT_LWO_ONE_LAYER_ONLY}</td><td> {@link #AI_CONFIG_IMPORT_MD5_NO_ANIM_AUTOLOAD}</td></tr><tr><td> {@link #AI_CONFIG_IMPORT_LWS_ANIM_START}</td><td> {@link #AI_CONFIG_IMPORT_LWS_ANIM_END}</td></tr><tr><td> {@link #AI_CONFIG_IMPORT_IRR_ANIM_FPS}</td><td> {@link #AI_CONFIG_IMPORT_OGRE_MATERIAL_FILE}</td></tr><tr><td> {@link #AI_CONFIG_IMPORT_OGRE_TEXTURETYPE_FROM_FILENAME}</td><td> {@link #AI_CONFIG_IMPORT_IFC_SKIP_SPACE_REPRESENTATIONS}</td></tr><tr><td> {@link #AI_CONFIG_ANDROID_JNI_ASSIMP_MANAGER_SUPPORT}</td><td> {@link #AI_CONFIG_IMPORT_IFC_SKIP_CURVE_REPRESENTATIONS}</td></tr><tr><td> {@link #AI_CONFIG_IMPORT_IFC_CUSTOM_TRIANGULATION}</td><td> {@link #AI_CONFIG_IMPORT_IFC_SMOOTHING_ANGLE}</td></tr><tr><td> {@link #AI_CONFIG_IMPORT_IFC_CYLINDRICAL_TESSELLATION}</td><td> {@link #AI_CONFIG_IMPORT_COLLADA_IGNORE_UP_DIRECTION}</td></tr><tr><td> {@link #AI_CONFIG_IMPORT_COLLADA_USE_COLLADA_NAMES}</td><td> {@link #AI_CONFIG_EXPORT_XFILE_64BIT}</td></tr><tr><td> {@link #AI_CONFIG_EXPORT_POINT_CLOUDS}</td><td> {@link #AI_CONFIG_GLOBAL_SCALE_FACTOR_KEY}</td></tr><tr><td> {@link #AI_CONFIG_GLOBAL_SCALE_FACTOR_DEFAULT}</td></tr></table>
 * @param value  New value for the property
 */
public static void aiSetImportPropertyMatrix(@NativeType("struct aiPropertyStore *") AIPropertyStore store,@NativeType("char const *") ByteBuffer szName,@NativeType("struct aiMatrix4x4 const *") AIMatrix4x4 value){
  if (CHECKS) {
    checkNT1(szName);
  }
  naiSetImportPropertyMatrix(store.address(),memAddress(szName),value.address());
}
